import { useState } from 'react';



export default function AddBookPage({addBook}) {


  const [form, setForm] = useState({
    name: '',
    reading: '',
    currentPage: 0,
    pageNote: '',
  })

  // const handleSubmit = (e) => {
  //   e.preventDefault()
