import { useState, useEffect } from 'react';
import * as booksApi from '../../utilities/books-api';

<div></div>
export default function AddBookPage({ books, setBooks }) {

  const [error, setError] = useState('')

  const [form, setForm] = useState({
    name: '',
    currentlyReading: 'Yes',
    currentPage: 'NaN',
    note: '',
  })

//   async function handleAddBook(evt) {
//     evt.preventDefault();
//     try {
//       console.log(form)
//       await booksApi.addBook(form)
//       setForm({
//         name: '',
//         currentlyReading: 'Yes',
//         currentPage: 'NaN',
//         note: '',
//       })
//     } catch {
//       setError('Sign Up Failed - Try Again');
//     }
//   };

// const addInfo = async () => {
//     fetch(`http://localhost:3000/api/books/${_id}`, {method:"PUT"})
//     .then(res => res.json())
//     .catch(err => console.log(err))
//     console.log('clicked')
//     }

function showData() {
    fetch('http://localhost:3000/api/books/show')
    .then(res => res.json())
    .then(data => setForm( data ))
    .catch(err => console.log(err))
  }

  useEffect(() => {
    showData()
    console.log("help?")
  }, []);

  console.log(books)

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value })
  }

  return (
    <>
      <h1>Edit Book Info!</h1>

        {/* <button onClick={addInfo}>test</button> */}
<br></br>
      <form className="Add-Book-Form">
        <label>Book Title:
          <input type="string" name="name" value={form.name} onChange={handleChange}></input>
        </label>
        <label>Currently Reading?
          <select name="currentlyReading" value={form.currentlyReading} onChange={handleChange}>
            <option value="Yes">Yes</option>
            <option value="No">No</option>
          </select>
        </label>
        <label>Current Page:
          <input type="number" name="currentPage" value={form.currentPage} onChange={handleChange} placeholder="1"></input>
        </label>
        <label>Page Note?
          <input type="string" name="note" value={form.note} onChange={handleChange} placeholder="thoughts?"></input>
        </label>
        <input type="submit" value="Add Book"></input>
      </form>
    </>
  );
}
