// import book from "../../../models/book";



export default function AddBookPage({books}) {

function handleSubmit(e) {
  e.preventDefault()
  console.log("submit?")
}

const [form, filledForm] = useState({
  name: '',
  reading: '',
  currentPage: 0,
  pageNote: '',
})

  return (
    <>
    <h1>Add Book</h1>
    <form action="/books/new" method="GET" onSubmit={handleSubmit}>
    <label>Book Title:
      <input type="string" name="name" value={form.name}></input>
    </label>
    <label>Currently Reading?
      <select name="reading" value={form.reading}>
        <option value="Yes">Yes</option>
        <option value="No">No</option>
      </select>
      </label>
      <label>Current Page:
        <input type="number" name="currentPage" value={form.currentPage} placeholder="1"></input>
    </label>
      <label>Page Note?
        <input type="string" name="pageNote"></input>
      </label>
    <input type="submit" value="Add Book"></input>
    </form>
    </>
  );
}
