// import book from "../../../models/book";
import { useState } from 'react';



export default function AddBookPage() {


  const [form, filledForm] = useState({
    name: '',
    reading: '',
    currentPage: 0,
    pageNote: '',
  })

  const handleSubmit = async(e) => {
    e.preventDefault()
    .then((response) => {return response.json})
    .then((json) => {console.log(json)})
    .catch((error) => {console.log(error)})
    return response
    console.log("submit?")
    filledForm(
      {
        name: '',
        reading: '',
        currentPage: 0,
        pageNote: '',
      }
    )
  }

const handleChange = (e) => {
  filledForm({...form, [e.target.name]:e.target.value})
  console.log(form)
}

  return (
    <>
    <h1>Add Book</h1>
    <form action="/books/new" method="POST" onSubmit={handleSubmit}>
    <label>Book Title:
      <input type="string" name="name" value={form.name} onChange={handleChange}></input>
    </label>
    <label>Currently Reading?
      <select name="reading" value={form.reading} onChange={handleChange}>
        <option value="Yes">Yes</option>
        <option value="No">No</option>
      </select>
      </label>
      <label>Current Page:
        <input type="number" name="currentPage" value={form.currentPage} onChange={handleChange}></input>
    </label>
      <label>Page Note?
        <input type="string" name="pageNote" value={form.pageNote} onChange={handleChange}></input>
      </label>
    <input type="submit" value="Add Book"></input>
    </form>
    </>
  );
}
